// import module "mongoose" for working with mongodb
var mongoose = require('mongoose');
var Schema = mongoose.Schema;

// set connection with db
mongoose.connect('mongodb://127.0.0.1:27017/mydb1');

// create Schema for numbers
var numberSchema = new Schema(
    {
        number: Number,
        date: Date // date, time - when number is added
    });

// create Schema for users
var userSchema = new mongoose.Schema(
    {
        username:
            {
                type: String,
                unique: true
            },
        password: String
    });

// export model to controller
module.exports = {
    ListOfNumbers: mongoose.model('ListOfNumbers', numberSchema),
    User: mongoose.model('User', userSchema)
};